mapper.select:
SELECT

FROM oa_overtime a

WHERE a.id = #{id}

SELECT

FROM oa_overtime a 


	a.del_flag = #{DEL_FLAG_NORMAL} AND a.user_id = #{user.id}



	AND u3.id LIKE 
			'%'||#{user.id}||'%'
			'%'+#{user.id}+'%'
			CONCAT('%', #{user.id}, '%')




	 AND a.begin_time > #{beginTime} AND a.end_time < #{endTime} 



	 AND a.begin_time > #{beginTime} 



	 AND a.end_time < #{endTime} 




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM oa_overtime a


	a.del_flag = #{DEL_FLAG_NORMAL} AND a.status = 1



	AND u3.id LIKE 
			'%'||#{user.id}||'%'
			'%'+#{user.id}+'%'
			CONCAT('%', #{user.id}, '%')




	 AND a.begin_time > #{beginTime} AND a.end_time < #{endTime} 



	 AND a.begin_time > #{beginTime} 



	 AND a.end_time < #{endTime} 




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM oa_overtime a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_overtime SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE oa_overtime SET
audit1_text = #{audit1Text},
update_by = #{updateBy.id},
update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_overtime SET
audit2_text = #{audit2Text},
update_by = #{updateBy.id},
update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_overtime SET
status = #{status}
WHERE id = #{id}

